insert into biz_cases(BC_NO,BC_TITLE,BC_CONTENT,BC_WRITER,BC_DATE,BC_HIT,INTENO) values(biz_cases_seq.nextval, '아이들을 위한 마법 같은 플레이룸', '오늘 포스팅에서 소개해 드릴 인테리어 시공 현장은 22평형 아파트입니다.이번 시공 현장은 전체적인 변화보다는 특정 공간에 초점을 맞춘 부분 리모델링으로 진행되었습니다.', '위디자인', systimestamp, 0, '24658');
insert into biz_cases(BC_NO,BC_TITLE,BC_CONTENT,BC_WRITER,BC_DATE,BC_HIT,INTENO) values(biz_cases_seq.nextval, '미니멀리스트의 철학: 깔끔하고 기능적인 공간 디자인', '이번에 소개해 드릴 시공 사례는 짙은 우드 마감재들을 걷어낸 후에 화이트 톤을 베이스로 선명하고 깨끗한 미니멀 인테리어로 지향해 꾸며질 수 있었던 현장이었는데요. 기존 주방 가벽 철거 시공과 함께 특히 단열, 방수에 신경 써 공사가 진행되었습니다 :D 49평형 구옥 아파트 인테리어 현장으로 넓은 공간이 미니멀 스타일로 어떻게 연출되었는지 지금부터 자세히 공간별로 소개해 드릴 테니 집중 부탁드립니다 :)', '위디자인', systimestamp, 0, '24658');
insert into biz_cases(BC_NO,BC_TITLE,BC_CONTENT,BC_WRITER,BC_DATE,BC_HIT,INTENO) values(biz_cases_seq.nextval, '산업적 로프트: 도시적 매력을 간직한 공간', '이번에는 감성이 살아 숨 쉬는 거실 공간에 대한 리모델링을 소개해 드립니다. 거실은 가족의 추억이 모이는 장소이자, 각자의 개성이 반영되는 쉼터입니다. 그래서 우리는 클라이언트의 취향을 세심하게 반영하여, 모두가 편안함을 느낄 수 있는 공간을 창조합니다. 오늘은 현대적이면서도 따뜻한 느낌을 주는 거실 디자인과 함께, 효율적인 공간 활용 팁을 알려드릴 예정이에요!', '위디자인', systimestamp, 0, '24658');
insert into biz_cases(BC_NO,BC_TITLE,BC_CONTENT,BC_WRITER,BC_DATE,BC_HIT,INTENO) values(biz_cases_seq.nextval, '아늑한 겨울 무드: 웜톤 인테리어로 추위 맞이', '이번에는 신혼부부를 위한 아늑한 침실 인테리어를 진행했던 현장을 소개해 드리려고 해요. 침실은 개인의 휴식을 취하는 곳으로 그 어떤 공간보다개인의 선호가 중요하게 작용하는데요, 우리는 두 분의 로맨틱하고 평화로운 분위기를 연출하기 위해 부드러운 색감과 섬세한 조명을 활용한 디자인을 제안했습니다. 침실 공간이 어떻게 변신했는지 지켜봐 주세요.', '위디자인', systimestamp, 0, '24658');
insert into biz_cases(BC_NO,BC_TITLE,BC_CONTENT,BC_WRITER,BC_DATE,BC_HIT,INTENO) values(biz_cases_seq.nextval, '레트로 모던: 과거와 현재의 완벽한 조화', '이번 포스트에서는 아이들을 위한 맞춤형 키즈룸 인테리어를 다뤄볼까 합니다. 아이들의 상상력과 창의력이 자라나는 곳, 바로 키즈룸이죠. 안전은 물론, 재미와 교육적 요소를 접목한 디자인으로 아이들이 매일 즐거운 모험을 할 수 있는 공간을 만들어냈습니다. 벽화, 다기능 수납장, 그리고 학습 공간까지, 아이들이 꿈꿀 수 있는 모든 것을 키즈룸에 담았어요!', '카멜레온디자인', systimestamp, 0, '95632');
insert into biz_cases(BC_NO,BC_TITLE,BC_CONTENT,BC_WRITER,BC_DATE,BC_HIT,INTENO) values(biz_cases_seq.nextval, '프렌치 쿠츠리: 프랑스 시골집 스타일', '오늘은 전원주택의 아름다운 서재 공간 인테리어를 공유하려 합니다. 서재는 조용하고 창의적인 생각이 필요한 공간이죠. 우리는 자연광을 최대한 활용하고, 나무의 따스함을 느낄 수 있는 내추럴한 소재를 사용해, 책과 함께 시간을 보내는 평화로움을 극대화했습니다. 휴식과 사색이 공존하는 서재 인테리어, 함께 살펴보시죠.', '카멜레온디자인', systimestamp, 0, '95632');
insert into biz_cases(BC_NO,BC_TITLE,BC_CONTENT,BC_WRITER,BC_DATE,BC_HIT,INTENO) values(biz_cases_seq.nextval, '감각적인 블랙 & 화이트 인테리어', '이번에는 작은 평수 아파트의 활용도를 극대화한 주방+다이닝 공간 리모델링 사례를 소개하려고 해요. 공간이 작다고 포기할 필요 없이, 우리는 스마트한 수납 해결책과 더불어 식사와 요리가 동시에 이루어질 수 있는 멀티펑셔널 공간을 설계했습니다. 주방과 식탁이 조화롭게 어우러져, 가족 모두가 즐겁게 요리하고 식사를 할 수 있는 분위기를 조성했죠. 이 작은 공간의 대변신, 놓치지 마세요!', '카멜레온디자인', systimestamp, 0, '95632');
insert into biz_cases(BC_NO,BC_TITLE,BC_CONTENT,BC_WRITER,BC_DATE,BC_HIT,INTENO) values(biz_cases_seq.nextval, '다기능 스튜디오 아파트: 작은 공간의 큰 변신', '지금부터 소개할 것은 현대적인 감각이 돋보이는 욕실 리모델링입니다. 욕실은 개인의 취향을 반영하는 동시에, 청결하고 기능적인 공간이어야 합니다. 저희는 클라이언트의 스타일을 반영해 최신 트렌드를 접목한 세련된 욕실을 제작했어요. 혁신적인 수납 솔루션과 세련된 타일 조합으로, 일상의 목욕 시간을 특별한 휴식의 시간으로 변모시켰습니다.', '카멜레온디자인', systimestamp, 0, '95632');
insert into biz_cases(BC_NO,BC_TITLE,BC_CONTENT,BC_WRITER,BC_DATE,BC_HIT,INTENO) values(biz_cases_seq.nextval, '동양의 정취: 아시아풍 인테리어 디자인', ' 이번에는 현대적 감각이 돋보이는 용산 파크뷰의 거실을 재해석한 인테리어 프로젝트를 선보일 예정입니다. 거실은 가정의 얼굴이자 휴식과 소통의 장으로서 그 가치가 더욱 중요해지고 있는데요, 우리는 클라이언트가 꿈꾸는 이상적인 거실 공간을 실현하기 위해 맞춤형 디자인을 제공합니다. 쾌적한 분위기 조성은 물론, 현대적인 가구 배치와 공간 활용의 팁을 통해, 가족 모두가 만족할 수 있는 거실 공간을 만들어 드리겠습니다!', '카멜레온디자인', systimestamp, 0, '95632');
insert into biz_cases(BC_NO,BC_TITLE,BC_CONTENT,BC_WRITER,BC_DATE,BC_HIT,INTENO) values(biz_cases_seq.nextval, '샤빌리 시크: 빈티지한 매력의 재발견', '고객님의 일상에 꼭 맞는 개성 있는 주거 공간을 제공합니다. 이번에는 트렌디한 감각이 돋보이는 명동 스카이 레지던스의 침실 인테리어를 소개해드리려 합니다. 침실은 개인의 휴식과 재충전을 위한 공간으로, 디자인과 기능성이 조화를 이루어야 하는 중요한 장소입니다. 맞춤형 수납장 설계로 깔끔함을 유지하면서도, 세련된 침실 디자인으로 여러분의 퀄리티 있는 삶을 지원하겠습니다. 컬러 매치부터 소품 선정까지, 디테일에 이르기까지 우리는 여러분의 침실이 단순한 잠자리가 아닌, 삶의 질을 높이는 공간이 되도록 만들어 갑니다!', '케이디자인', systimestamp, 0, '24587');
insert into biz_cases(BC_NO,BC_TITLE,BC_CONTENT,BC_WRITER,BC_DATE,BC_HIT,INTENO) values(biz_cases_seq.nextval, '서핑 엔스우지아스트를 위한 해변 스타일 홈', '고객님의 이야기가 담긴 생활 공간을 만드는 데 집중합니다. 오늘은 근사한 강남 엘리트 하우스의 아이 방 리모델링 프로젝트를 공개합니다. 아이 방은 아이의 성장은 물론, 학습과 놀이가 함께하는 다용도 공간으로 기능해야 합니다. 우리는 고객님의 아이가 즐겁고 건강하게 자랄 수 있는 환경을 만들기 위해 안전한 재료 선택과 창의적인 공간 배치를 추구합니다. 학습 효율을 높여주는 책상 배치부터, 재미있는 놀이 공간까지, 아이의 성장 과정에 맞춘 인테리어로 가득 채울 것입니다!', '케이디자인', systimestamp, 0, '24587');
insert into biz_cases(BC_NO,BC_TITLE,BC_CONTENT,BC_WRITER,BC_DATE,BC_HIT,INTENO) values(biz_cases_seq.nextval, '차분하고 명상적인 공간: 젠 스타일 인테리어', '오늘은 특별히 세련된 도시적 감성을 담은 한남동 레지던스의 거실 인테리어를 소개해 드릴 예정입니다. 이 공간은 도심 속 현대적 생활을 반영해 모더니티와 편안함을 조화롭게 담아내고자 했습니다. 집안 곳곳에 세심하게 배치된 아트워크와 함께, 휴식과 대화가 어우러지는 거실 공간을 만들어 더욱 의미 있는 시간을 보낼 수 있도록 설계했습니다.', '케이디자인', systimestamp, 0, '24587');
insert into biz_cases(BC_NO,BC_TITLE,BC_CONTENT,BC_WRITER,BC_DATE,BC_HIT,INTENO) values(biz_cases_seq.nextval, '그리스 로맨틱: 지중해 스타일 거실', '이번에는 자연 채광을 극대화한 서초동 펜트하우스의 침실 인테리어를 보여드릴 것입니다. 이 공간은 대형 창문을 통해 들어오는 햇빛을 이용해 여유로운 아침을 맞이하실 수 있도록 디자인되었습니다. 우드 소재와 화이트 컬러를 기반으로 한 인테리어가 포근하고 안정된 수면을 도와드릴 거예요.', '케이디자인', systimestamp, 0, '24587');
insert into biz_cases(BC_NO,BC_TITLE,BC_CONTENT,BC_WRITER,BC_DATE,BC_HIT,INTENO) values(biz_cases_seq.nextval, '생기를 불어넣는 실내 정원 디자인', '오늘은 현대와 전통이 공존하는 분위기를 연출한 북촌 한옥의 다락방 인테리어를 선보이고자 합니다. 전통적인 요소를 현대적 감각으로 재해석하여, 전통의 멋과 현대적 편의성이 어우러진 고유의 공간을 만들어냈습니다. 목재와 한지를 사용한 인테리어로 고즈넉한 분위기 속에서 여유를 만끽하실 수 있습니다.', '케이디자인', systimestamp, 0, '24587');
insert into biz_cases(BC_NO,BC_TITLE,BC_CONTENT,BC_WRITER,BC_DATE,BC_HIT,INTENO) values(biz_cases_seq.nextval, '모듈러 가구와 함께하는 현대적 거주 공간', '오늘은 활기찬 색채가 돋보이는 강남 스타일리스트 살롱의 내부 인테리어를 소개해 드립니다. 이곳은 고객님의 에너지와 창의력이 샘솟도록 활기찬 색상과 패턴으로 가득 차 있습니다.', '디자인grow', systimestamp, 0, '79852');
insert into biz_cases(BC_NO,BC_TITLE,BC_CONTENT,BC_WRITER,BC_DATE,BC_HIT,INTENO) values(biz_cases_seq.nextval, '뉴욕 로프트: 시티 라이프의 정수', '도심 한복판에서 자연과 어우러지는 휴식을 취할 수 있는 거실 인테리어를 소개해 드리고자 합니다. 우리는 집안에 식물을 풍성하게 배치하고, 자연 소재를 사용하여 공간에 생명을 불어넣었습니다. 햇빛이 잘 들어오는 창가에는 작은 허브 정원을 마련하여 신선함을 느낄 수 있도록 했죠. 자연스러운 컬러 팔레트와 목재의 따뜻함이 조화를 이루는 이 거실은 마치 숲속의 캐빈에 있는 듯한 평화로움을 선사합니다.', '디자인grow', systimestamp, 0, '79852');
insert into biz_cases(BC_NO,BC_TITLE,BC_CONTENT,BC_WRITER,BC_DATE,BC_HIT,INTENO) values(biz_cases_seq.nextval, '매혹적인 앤틱 가구 컬렉션의 집', '최근 재택근무가 일상이 된 만큼, 집에서도 업무에 집중할 수 있는 공간이 필수가 되었죠. 오늘은 고객님의 업무 효율성을 극대화하기 위해 디자인한 미니멀리즘 홈 오피스를 공유하려 합니다. 흰색과 검정색의 대조적인 컬러 스킴을 사용하여 집중을 방해하는 요소를 최소화했습니다. 또한, 맞춤형 책상과 수납장을 통해 최대한의 수납 공간을 확보하면서도 깔끔한 라인을 유지했습니다. 단순하지만 강력한 이 공간은 효율적인 작업환경을 만드는 데 중점을 두었습니다.', '디자인grow', systimestamp, 0, '79852');
insert into biz_cases(BC_NO,BC_TITLE,BC_CONTENT,BC_WRITER,BC_DATE,BC_HIT,INTENO) values(biz_cases_seq.nextval, '코티지 스타일: 자연과 함께하는 집', '오늘은 특별히 연인들을 위한 로맨틱한 침실 인테리어를 소개해 드립니다. 부드러운 조명, 따스한 패브릭, 그리고 섬세한 장식으로 이루어진 이 침실은 두 사람만의 달콤한 공간이 되도록 설계되었습니다. 벽면 하나를 선택하여 사진과 추억이 담긴 갤러리 벽을 만들고, 부드러운 커튼과 침구로 아늑함을 더했습니다. 이곳은 일상에서 벗어나 서로를 위한 시간을 만끽할 수 있는 완벽한 공간입니다.', '디자인grow', systimestamp, 0, '79852');
insert into biz_cases(BC_NO,BC_TITLE,BC_CONTENT,BC_WRITER,BC_DATE,BC_HIT,INTENO) values(biz_cases_seq.nextval, '패션 디자이너의 스튜디오: 창조적 영감을 자극하는 공간 디자인', '오늘은 기능성과 스타일이 조화를 이룬 현대적인 주방 리모델링 사례를 보여드립니다. 고객님의 요리 활동을 고려하여 실용적인 구조의 주방을 설계하였고, 최신 가전제품을 통합하여 현대적인 편의성을 더했습니다. 세련된 메탈릭 터치와 모던한 캐비닛 디자인으로 깔끔하면서도 풍부한 저장 공간을 제공합니다. 이 주방은 모임의 중심지가 될 것이며 가족 간의 소통을 더욱 풍부하게 해줄 것입니다.', '디자인grow', systimestamp, 0, '79852');
